import React, { Component } from 'react';
import Card from './Card/Card'
import './App.css'
import Header from './Header/Header'
import Calc from './Calc/Calc'




class App extends Component {
    constructor(props){
        super(props);
        this.state = {
        Val: [0],
        ActiveVal1 : 'SEK',
        ActiveVal2 : 'USD',
        Cost2 : 0,
        Sum: 0,
        Col1: ['Col1'],
        Col2: ['Col2'],
        Col3: ['Col3'],
        };
    }



    changeSum = () => {
        let c = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal1);
        let d = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal2);
        let newCost = (this.state.Sum * c[0].Value / d[0].Value ).toFixed(3) 
        this.setState({
            Cost2: newCost,
        })
    }


    changeActiveVal1 = (newVal) => {
        this.setState({
            ActiveVal1: newVal,
        })
        this.changeSum()
    }

    changeActiveVal2 = (newVal) => {
        this.setState({
            ActiveVal2: newVal,
        })
        let c = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal1);
        let d = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal2);
        let newSum = (this.state.Cost2 / c[0].Value * d[0].Value ).toFixed(3) 
        this.setState({

            Sum: newSum,
        })
    }

    changeCost = (event) => {

        let c = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal1);
        let d = this.state.Val.filter ((i)=> i.CharCode === this.state.ActiveVal2);
        let sum = event.target.value
        let newCost = (event.target.value * c[0].Value / d[0].Value ).toFixed(3) 
         this.setState ({
             Cost2: newCost,
             Sum : sum,
         })       
    }

    changeWallet = (event) => {
        let wal = event.target.value 
        this.setState ({ActiveWall: wal})
    }

    Close = () => {
        const arr = this.state.Col3
        arr.push('Closed')
        const arr1 = this.state.Col1
        arr1.push('Closed')
        const arr3 = this.state.Col2
        arr3.push('Closed')
        const arr2 = this.state.Col4
        arr2.pop()
        this.setState({
            Col3: arr,
            Col4: arr2,
            Col1: arr1,
            Col2: arr3,
        })
    }


    render() {


    return ( 
        <div style ={{textAlign: 'center'}}>
            <Header />
            <div className='AllVals'>
                <Card 
                change = {this.changeActiveVal1.bind(card.CharCode)}
                />

                <div className={this.state.Col3.join(' ')}>
                    <Calc 
                    val1={this.state.ActiveVal1} 
                    val2={this.state.ActiveVal2} 
                    onChange = {this.changeCost} 
                    cost1 = {this.state.Sum} 
                    cost2 = {this.state.Cost2} 
                    Close = {this.Close} 
                    onChangeWallet = {this.changeWallet} />
                </div>

                <div className={this.state.Col2.join(' ')}>
                    Вы получите 

                    { this.state.Val.map((card, index) => {
                        return ( 
                            <Card
                            key = {index} 
                            name = {card.Name} 
                            symbol = {card.CharCode}
                            onClick1 = {this.changeActiveVal2.bind(this, card.CharCode)}
                            />
                        )
                    }) 
                    }

                    </div>

            </div>
        </div>
    )

    }
}
export default App;
